import { styled } from "styled-components";

export const PopupCheckoutContainer = styled.div`
	background: linear-gradient(to right bottom, #152d32cc, #001f24cc);
	background-size: cover;
    background-position: center;
    position: absolute;
	top: 0;
	left: 0;
	height: 100vh;
	width: 100vw;
	display: flex;
	justify-content: center;
	align-items: center;
`

export const TableContainer = styled.div`
	width: 500px;
	height: 200px;
	background: white;
	display: flex;
	justify-content: space-between;
`

export const TextContainer = styled.div`
	font-size: 30px;
`

export const PayContainer = styled.div`
	width: 40%;
	display: flex;
	flex-direction: column;
`
