import {useSelector} from "react-redux";
import {selectNFTtoBuy} from "../../redux/nft/nft.selectors";
import {selectCurrentUser} from "../../redux/user/user.selectors";
import CustomButton from "../custom-buttons/custom-button.component";

import {PopupCheckoutContainer, TableContainer, PayContainer, TextContainer} from "./popup-checkout.styles"


const PopupCheckout = () => {
	const nftTobuy = useSelector(selectNFTtoBuy)
	const currentUser = useSelector(selectCurrentUser)

	const {commission_rate} = currentUser
	const {price} = nftTobuy
	return (
		<PopupCheckoutContainer> 
			<TableContainer>
				<PayContainer>
					<h4>Commission In Ethereum</h4>	
					<h4>Total is </h4>
					<TextContainer> {price + commission_rate} ETH</TextContainer>	
					<TextContainer> $0 USD</TextContainer>	
					<CustomButton> Pay </CustomButton>
				</PayContainer>
				<PayContainer>
					<h4>Commission In USD </h4>	
					<h4>Total is </h4>
					<TextContainer>{price} ETH </TextContainer>	
					<CustomButton> Pay </CustomButton>
				</PayContainer>
			</TableContainer>
		</PopupCheckoutContainer>
	)
}

export default PopupCheckout;
