import React, {useEffect} from 'react'
import {useSelector, useDispatch} from 'react-redux';
import Header from '../../components/header/header.component';
import {selectNFTs} from '../../redux/nft/nft.selectors';
import {fetchNFTTransactionStart} from '../../redux/transaction/transaction.actions';
import {selectNFTTransactions} from '../../redux/transaction/transaction.selectors';
import {selectCurrentUser} from '../../redux/user/user.selectors';
import { shortenAddress, getCentralTime } from '../../utils';

import {
	TransactionContainer,
	HeaderContainer,
	TransferContainer,
	TextContainer
} from './transaction-page.styles';

const TransactionPage = () => {
	const dispatch = useDispatch()

	useEffect(() => {
		dispatch(fetchNFTTransactionStart())
	}, [dispatch]);

	const transactions = useSelector(selectNFTTransactions)	
	const nfts = useSelector(selectNFTs)
	const currentUser = useSelector(selectCurrentUser)

	const filteredTransactions = transactions.filter(transaction => transaction.buyer === currentUser.trader_id);

    return (
        <TransactionContainer>
			<Header/>
			<HeaderContainer>Transaction History</HeaderContainer>
			<TransferContainer>
					<TextContainer>Contract address </TextContainer>
					<TextContainer>Token ID</TextContainer>
					<TextContainer>Date</TextContainer>
					<TextContainer>From</TextContainer>
					<TextContainer>To</TextContainer>
					<TextContainer>Value</TextContainer>
					<TextContainer>Fee</TextContainer>
			</TransferContainer>
		{
			filteredTransactions.map(({commission_fee, transaction_amount, contract_address, 
				buyer, seller, transaction_timestamp, nft_id}, i) => ( 
				<TransferContainer>
						<TextContainer>{shortenAddress(contract_address)}</TextContainer>
						<TextContainer>{nfts[nft_id-1].token_id}</TextContainer>
						<TextContainer>{getCentralTime(transaction_timestamp).slice(0,10)}</TextContainer>
						<TextContainer>{transaction_amount}</TextContainer>
						<TextContainer>{getCentralTime(transaction_timestamp).slice(0,10)}</TextContainer>
						<TextContainer>{transaction_amount}</TextContainer>
				</TransferContainer>
			))
		}
        </TransactionContainer>
)}


export default TransactionPage;
