import {useEffect, useState} from 'react';
import {useParams} from 'react-router-dom';
import {useSelector, useDispatch} from 'react-redux';
import {fetchNFTStart} from '../../redux/nft/nft.actions';
import {selectNFTs} from '../../redux/nft/nft.selectors';
import {selectCollections} from '../../redux/collection/collection.selectors';
import {getCollectionIDByName} from '../../redux/collection/collection.utils';
import CardList from '../../components/card-list/card-list.component';
import SearchBox from '../../components/search-box/search-box.component';

const CollectionPage = () => {
	const dispatch = useDispatch();
	const collectionName = useParams().collectionName;
	const collections = useSelector(selectCollections);
	const nfts = useSelector(selectNFTs);

	const [searchField, setSearchField] = useState('');

	const [filteredNFTs, setFilterNFTs] = useState(nfts);

	useEffect(() => {
		const newFilteredNFTs = nfts.filter(nft => nft.collection_id == getCollectionIDByName(collections, collectionName));

		setFilterNFTs(newFilteredNFTs);
	}, [nfts, collectionName]);

	useEffect(() => {
		const newFilteredNFTs = nfts.filter(nft => nft.collection_id == getCollectionIDByName(collections, collectionName));

		setFilterNFTs(newFilteredNFTs);
		const newFilteredNFTsByTokenID = filteredNFTs.filter(nft => nft.token_id.toString().includes(searchField));

		setFilterNFTs(newFilteredNFTsByTokenID);
	}, [filteredNFTs, searchField]);

	const onSearchChange = event => {
		const searchFieldString = event.target.value.toLocaleLowerCase();
		setSearchField(searchFieldString);
	};

	return (
		<div>
			<SearchBox
				className='monsters-search-box'
				onChangeHandler={onSearchChange}
				placeholder='search monsters'
			/>
			<CardList nfts={filteredNFTs} />
		</div>
	);
};

export default CollectionPage;
