import UserActionTypes from "./nft.types";

const INITIAL_STATE = {
    isFetching: true,
    users: [],
    message: ''
};

const userReducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case UserActionTypes.FETCH_USER_START:
            return {
                ...state,
                isFetching: true
            };
        case UserActionTypes.FETCH_USER_SUCCESS:
            return {
                ...state,
                isFetching: false,
                nfts: action.payload
            };
        case UserActionTypes.FETCH_USER_FAILURE:
            return {
                ...state,
                isFetching: false,
                message: action.payload
            };
        default:
            return state;
    }
};

export default userReducer;
