import React, {useState} from 'react';

import {useParams} from 'react-router-dom';

import FormInput from '../../components/form-input/form-input.component' 
import CustomButton from '../../components/custom-buttons/custom-button.component';

import { TitleContainer, SignUpContainer, ButtonsContainer } from './sign-up-page.styles';

const SignUpPage = () => {
	const traderID = useParams().traderID;

	const [userCredentials, setUserCredentials] = useState({
        first_name: "",
        last_name: "",
		phone_number: "",
		cell_phone_number: "",
		email_address: "",
		street_address: "",
		city: "",
		state: "",
		zip_code: "",
		ethereum_address: "",
    });

	const {first_name, last_name, phone_number} = userCredentials;

	const handleSubmit = event => {
		event.preventDefault();
		console.log(event, 1);
		// Send login request to the server
		fetch('http://localhost:3002/signup/1', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({first_name, last_name}),
		})
			.then(response => response.json())
			.then(data => {
				console.log(data)
				if(data.success) {
					alert("sign up successfully")
					// navigate('/')
				} else {
					alert("your password or username is invalid")
				}
				console.log(data.result[0].trader_id);
				// Handle the login response accordingly
				// For simplicity, just logging the response to the console
			})
			.catch(error => {
				console.error('Error:', error);
			});
	};

	const handleChange = event => {
		const {name, value} = event.target;
		setUserCredentials({...userCredentials, [name]: value});
	};

	return (
		<SignUpContainer>
			<TitleContainer>
                Sign up 
			</TitleContainer>
			<form onSubmit={handleSubmit}>
				<FormInput
					handleChange={handleChange}
					label='first name'
					name='first_name'
					type='text'
					value={first_name}
					required
				/>
				<FormInput
					handleChange={handleChange}
					label='last name'
					name='last_name'
					type='text'
					value={last_name}
					required
				/>
				<FormInput
					handleChange={handleChange}
					label='phone number'
					name='phone_number'
					type='text'
					value={phone_number}
					required
				/>
				<ButtonsContainer>
					<CustomButton type='submit'>Sign UP</CustomButton>
				</ButtonsContainer>
			</form>
		</SignUpContainer>
	);
};

export default SignUpPage;
