import {useSelector} from 'react-redux';
import {useLocation} from 'react-router-dom';

import {selectCollections} from '../../redux/collection/collection.selectors';
import {getNameByCollectionID} from '../../redux/collection/collection.utils';
import { AddButton, CardContainer } from './card.styles';

const Card = ({nft}) => {
	const {token_id, url_image, collection_id} = nft;
	const location = useLocation();
	const collections = useSelector(selectCollections)
	const name = getNameByCollectionID(collections, collection_id)
	return (
		<CardContainer>
			<img
				alt={`NFT ${token_id}`}
				src={`${url_image}`}
				height = 'auto'
				width = '100%'
			/>
			<h2>{name} {token_id}</h2>
			<AddButton>Add to card </AddButton>	
		</CardContainer>
	);
};

export default Card;
