import { useState } from "react";
import {useDispatch, useSelector} from "react-redux";

import FormInput from "../../components/form-input/form-input.component";

import { TitleContainer } from "./deposit-page.styles";

const DepositPage = () =>{
	const dispatch = useDispatch()

	const [payment, setPayment] = useState({
		amount: '',
		paymentAddress: '',
	});

	const {amount, paymentAddress} = payment;

	const handleChange = event => {
		const {name, value} = event.target;
		setPayment({...payment, [name]: value});
	};

		const handleSubmit = event => {
		event.preventDefault();
		console.log(event, 1);
		// Send login request to the server
		fetch('http://localhost:3002/signin', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({username, password}),
		})
			.then(response => response.json())
			.then(data => {
				console.log(data)
				if(data.success) {
					const newUser = users[data.result[0].trader_id]
					newUser['balance'] = data.result[0].balance
					newUser['ethereum_balance'] = data.result[0].ethereum_balance
					dispatch(setCurrentUser(newUser))	
					navigate('/')
				} else {
					alert("your username or password is incorrect")
				}
				console.log(data.result[0].trader_id);
				// Handle the login response accordingly
				// For simplicity, just logging the response to the console
			})
			.catch(error => {
				console.error('Error:', error);
			});
	};


	return (
		<div>
			<TitleContainer>I already have an account</TitleContainer>
			<TitleContainer>
                Sign in with your email and password
			</TitleContainer>
			<form >
				<FormInput
					handleChange={handleChange}
					label='paymentAddress'
					name='paymentAddress'
					type='text'
					value={paymentAddress}
					required
				/>
				<FormInput
					handleChange={handleChange}
					label='amount'
					name='amount'
					type='text'
					value={amount}
					required
				/>
				<ButtonsContainer>
					<CustomButton type='submit'>Submit</CustomButton>
				</ButtonsContainer>
			</form>
		</div>
	);
}

export default DepositPage;
