import { useState } from "react";
import {useDispatch, useSelector} from "react-redux";

import FormInput from "../../components/form-input/form-input.component";

import { TitleContainer, ButtonsContainer, DepositContainer } from "./deposit-page.styles";
import CustomButton from "../../components/custom-buttons/custom-button.component";

const DepositPage = () =>{

	const [payment, setPayment] = useState({
		amount: '',
		paymentAddress: '',
	});

	const {amount, paymentAddress} = payment;

	const handleChange = event => {
		const {name, value} = event.target;
		setPayment({...payment, [name]: value});
	};



	return (
		<DepositContainer>
			<TitleContainer>
				Deposit by Bank transfer 
			</TitleContainer>
			<form>
				<FormInput
					handleChange={handleChange}
					label='paymentAddress'
					name='paymentAddress'
					type='text'
					value={paymentAddress}
					required
				/>
				<FormInput
					handleChange={handleChange}
					label='amount'
					name='amount'
					type='text'
					value={amount}
					required
				/>
				<ButtonsContainer>
					<CustomButton type='submit'>Submit</CustomButton>
				</ButtonsContainer>
			</form>
		</DepositContainer>
	);
}

export default DepositPage;
