import {useState} from 'react';
import axios from 'axios';
import {useDispatch, useSelector} from 'react-redux';
import FormInput from '../../components/form-input/form-input.component';
import CustomButton from '../../components/custom-buttons/custom-button.component';
import {selectCurrentUser} from '../../redux/user/user.selectors';
import {TitleContainer, ButtonsContainer, DepositContainer} from './deposit-bank.styles';

const DepositBank = () => {
	const currentUser = useSelector(selectCurrentUser);
	const {trader_id} = currentUser;

	const [payment, setPayment] = useState({
		amount: '',
		paymentAddress: '',
	});

	const {amount, paymentAddress} = payment;

	const handleChange = event => {
		const {name, value} = event.target;
		setPayment({...payment, [name]: value});
	};

	const handleSubmit = () => {
		axios.put(`http://localhost:3002/deposit/${trader_id}`, {amount, paymentAddress})
			.then(response => {
				console.log(response.data);
				// Handle success, e.g., show a success message to the user
			})
			.catch(error => {
				console.error('Error increasing amount:', error);
				// Handle error, e.g., show an error message to the user
			});
	}

		return (
			<DepositContainer>
				<TitleContainer>
				Deposit by Bank transfer
				</TitleContainer>
				<form onSubmit={handleSubmit}>
					<FormInput
						handleChange={handleChange}
						label='Payment Address'
						name='paymentAddress'
						type='text'
						value={paymentAddress}
						required
					/>
					<FormInput
						handleChange={handleChange}
						label='Amount'
						name='amount'
						type='text'
						value={amount}
						required
					/>
					<ButtonsContainer>
						<CustomButton type='submit'>Submit</CustomButton>
					</ButtonsContainer>
				</form>
			</DepositContainer>
		);
	};


export default DepositBank;
