import { takeLatest, put, call } from 'redux-saga/effects';
import axios from 'axios';

import { fetchCollectionSuccess, fetchCollectionFailure } from './collection.actions'

import CollectionActionTypes from './collection.types';

function* fetchCollection() {
  try {
    const collections = yield call(axios.get, '/api/getCollections'); // Adjust the API endpoint as needed
    yield put(fetchCollectionSuccess(collections))
  } catch (error) {
	yield put(fetchCollectionFailure(error.message));
  }
}

// export function*

export function* fetchCollectionStart() {
    yield takeLatest(CollectionActionTypes.FETCH_COLLECTION_START, fetchCollection());
}
