import { useState, useEffect } from 'react';

import { Route, Switch } from 'react-router-dom';

import CardList from './components/card-list/card-list.component';
import './App.css';

const HomePage = () => {
	const [searchField, setSearchField] = useState('');
	const [NFTs, setNFTs] = useState([]);
	const [filteredNFTs, setFilterNFTs] = useState(NFTs);

	useEffect(() => {
		fetch('http://localhost:3002/api/get')
			.then((response) => response.json())
			.then((nfts) => setNFTs(nfts));
	}, []);

	console.log(NFTs)
	useEffect(() => {
		const newFilteredNFTs = NFTs.filter((nft) => {
			return nft.contract_address.includes(searchField);
		});

		setFilterNFTs(newFilteredNFTs);
	}, [NFTs, searchField]);

	const onSearchChange = (event) => {
		const searchFieldString = event.target.value.toLocaleLowerCase();
		setSearchField(searchFieldString);
	};

	return (
		<div className='App'>
			<SearchBox
				className='monsters-search-box'
				onChangeHandler={onSearchChange}
				placeholder='search monsters'
			/>
			<Collection NFTs={filteredNFTs} />
		</div>
	);
};

export default App;
